<?php defined('SYSPATH') OR die('No direct script access.');/** * Генерируем меню */// Запрошенный URI$uri = Request::$initial->uri() === '/' ? '' : Request::$initial->uri();// Данные меню$data = array(	array('text' => 'Новые', 'link' => '/', 'regex' => '(page\/\d+)?'),	array('text' => 'Лучшие', 'link' => '/best', 'regex' => 'best(\/page\/\d+)?'),	array('text' => 'Авторы', 'link' => '/author', 'regex' => 'author'),);$html = "<ul>";foreach ($data as $menu){	// Выбранный раздел меню	$selected = "";	if ($menu['regex'] !== NULL)	{		if (preg_match('/^' . $menu['regex'] . '$/i', $uri))		{			$selected = " class='selected'";		}	}	$html .= "<li" . $selected . "><a href='" . $menu['link'] . "'>" . $menu['text'] . "</a></li>";}$html .= "</ul>";echo $html;